void split(const DTYPE in[SIZE], DTYPE out1[SIZE], DTYPE out2[SIZE]){
	SplitLoop:
	for(int i = 0; i < SIZE; i++){
		DTYPE in_current = in[i];
		out1[i] = in_current;
		out2[i] = in_current;
	}
}

void calculate_output_r(DTYPE INPUT_R[SIZE], DTYPE INPUT_I[SIZE], DTYPE OUTPUT_R[SIZE], DTYPE cos[SIZE], DTYPE sin[SIZE]){
	RLoop:
	for(int i = 0; i < SIZE; i++){
		for(int j = 0; j < SIZE; j++){
			DTYPE cos_current = cos[i*j%SIZE];
			DTYPE sin_current = sin[i*j%SIZE];
			DTYPE INPUT_R_current = INPUT_R[j];
			DTYPE INPUT_I_current = INPUT_I[j];
			OUTPUT_R[i]  = (j==0) ? 0 : OUTPUT_R[i] + INPUT_R_current*cos_current - INPUT_I_current*sin_current;

		}
	}
}

void calculate_output_i(DTYPE INPUT_R[SIZE], DTYPE INPUT_I[SIZE], DTYPE OUTPUT_I[SIZE], DTYPE cos[SIZE], DTYPE sin[SIZE]){
	ILoop:
	for(int i = 0; i < SIZE; i++){
		for(int j = 0; j < SIZE; j++){
			DTYPE cos_current = cos[i*j%SIZE];
			DTYPE sin_current = sin[i*j%SIZE];
			DTYPE INPUT_R_current = INPUT_R[j];
			DTYPE INPUT_I_current = INPUT_I[j];
			OUTPUT_I[i]  = (j==0) ? 0 : OUTPUT_I[i] + INPUT_R_current*sin_current + INPUT_I_current*cos_current;
		}
	}
}


